<?php 	//date_default_timezone_set('EST');		//store each exercise in a string variable	$excerise1 = 'Display "Hello World"';	$excerise2 = 'Convert Pounds to Kilograms';	$excerise3 = 'Convert Kilograms to Pounds';	$excerise4 = 'Convert Miles to Kilometers';	$excerise5 = 'Convert Kilometers to Miles';	$excerise6 = 'Month long string of the day';	$excerise7 = 'String of the day with levels';	//create a variable containing the day of the week	$day = date ('l');		//use an if statement to test for the day of the week	if ($day == 1) {		echo $excerise1;	} elseif ($day == 2) {		echo $excerise2;	} elseif ($day == 3) {		echo $excerise3;	} elseif ($day == 4) {		echo $excerise4;	} elseif ($day == 5) {		echo $excerise5;	} elseif ($day == 6) {		echo $excerise6;	} elseif ($day == 7) {		echo $excerise7;	}	// display the corresponding excerise string							?>